import './App.css';
import {useState} from 'react';
import {useRef} from 'react'
import Heading from './Heading.jsx';
// import Heading2 from './Heading2.jsx';
import SecondHeader from './SecondHeader';
import DownButton from './DownButton';



function App() {

  const [isShown, setIsShown] = useState(false);
  const ref = useRef(null);

  const handleScrollDownOnClick = () => {
    setIsShown(true);
    console.log("Showing Div");
  }

  const handleClick = () => {
    ref.current?.scrollIntoView({behavior: 'smooth'})
    handleScrollDownOnClick()
    console.log("Scrollling Down")
  }

  return (
  <div>
    <div class = "flex flex-col h-screen justify-center items-center bg-gradient-to-r from-violet-500 to-fuchsia-500">
      <Heading/>
    <div 
      onClick={handleClick} class = "cursor-pointer mt-96 drop-shadow-2xl animate-bounce">
      <DownButton />
    </div>
    </div>
    {isShown && (<div class="flex flex-col h-screen items-center bg-gradient-to-r from-violet-500 to-fuchsia-500">
      <div ref = {ref}>
        <SecondHeader/>
      </div>
    </div>)}
  </div>
  );
}

export default App;
