import './App.css';
import {useRef} from 'react'
import Heading from './Heading.jsx';
import Heading2 from './Heading2.jsx';
import SecondHeader from './SecondHeader';
import DownButton from './DownButton';



function App() {
  const ref = useRef(null);

  const handleClick = () => {
    ref.current?.scrollIntoView({behavior: 'smooth'});
  }

  return (
  <div>
    <div class = "flex flex-col h-screen justify-center items-center bg-gradient-to-r from-violet-500 to-fuchsia-500">
      <Heading/>
    <div  onClick={handleClick} class = "justify-center items-center cursor-pointer mt-96 drop-shadow-2xl animate-bounce	">
      <DownButton/>
    </div>
    </div>
    <div class="flex flex-col h-screen items-center bg-gradient-to-r from-violet-500 to-fuchsia-500">
      <div ref = {ref}>
        <SecondHeader/>
      </div>
    </div>
  </div>
  );
}

export default App;
